mapper.update:
		UPDATE
			TB_MBR_MST
		SET
			PASSWD = #{passwd}
		WHERE
			EMAIL = #{email}
        	AND MBR_NM = #{mbr_nm}
        	AND ID = #{id}

mapper.select:
		SELECT 
		 	PASSWD 
        FROM 
        	TB_MBR_MST
        WHERE 
        	EMAIL = #{email}
        	AND MBR_NM = #{mbr_nm}
        	AND ID = #{id}

mapper.update:
UPDATE
	TB_MBR_MST
SET
	PASSWD		= #{passwd}
	, UPDATER	= #{updater}
	, DT_UPT	= SYSDATE
WHERE
	SEQ_MBR		= #{seq_mbr}

UPDATE
	TB_MBR_MST
SET
	DT_UPT	= SYSDATE
	
	, PHONE	= #{phone}
	
	, UPDATER = #{updater}
WHERE
	SEQ_MBR	= #{seq_mbr}

UPDATE
	TB_MBR_DTL
SET
	DT_UPT		= SYSDATE
	, UPDATER	= #{updater}
	
	, POST		= #{post}
	
	
	, ADDR1		= #{addr1}
	
	
	, ADDR2		= #{addr2}
	
	
	, PETS	= #{pets}
	
	
	, FLG_SMS	= #{flg_sms}
	, DT_SMS	= SYSDATE
	
	
	, FLG_EMAIL	= #{flg_email}
	, DT_EMAIL	= SYSDATE
	
WHERE
	SEQ_MBR		= #{seq_mbr}
